    "6-13": { status: 'NP', statusFull: "Idus", deity: "Apollo", deityDesc: "Apolo.", festivalName: "Ludi Apollinares", festivalDesc: "Conclusión de los Juegos de Apolo. Se pide salud y protección solar." },
    "6-14": { status: 'F', statusFull: "Dies Fastus", deity: "Fortuna", deityDesc: "Fortuna.", festivalName: "Mercatus", festivalDesc: "Días de mercado tras los juegos religiosos." },
    "6-15": { status: 'C', statusFull: "Dies Comitialis", deity: "Castor y Pólux", deityDesc: "Caballeros romanos.", festivalName: "Transvectio Equitum", festivalDesc: "Gran desfile de la caballería romana (Equites) en honor a Cástor y Pólux. Los jinetes visten togas trabeas y coronas de olivo, cabalgando desde el Templo de Marte hasta el Capitolio. Exhibición del poder militar aristocrático." },
    "6-16": { status: 'F', statusFull: "Dies Fastus", deity: "Hecate", deityDesc: "Diosa de las encrucijadas.", festivalName: null, festivalDesc: null },
    "6-17": { status: 'C', statusFull: "Dies Comitialis", deity: "Honos", deityDesc: "El Honor.", festivalName: "Dedicación Templo Honos", festivalDesc: "Fuera de la Puerta Collina." },
    "6-18": { status: 'C', statusFull: "Dies Comitialis", deity: "Victory", deityDesc: "Victoria.", festivalName: "Dies Alliensis", festivalDesc: "¡Dies Ater! Día negro en el calendario. Aniversario de la derrota del río Allia contra los galos (390 a.C.). No se realizan negocios, ni sacrificios públicos, ni batallas si es posible. Un día de luto nacional." },
    "6-19": { status: 'NP', statusFull: "Lucaria", deity: "Silvanus", deityDesc: "Dios de los bosques.", festivalName: "Lucaria", festivalDesc: "Fiesta de los bosques sagrados (lucus). Se celebra en el gran bosque entre la Vía Salaria y el Tíber, donde los romanos se escondieron tras la derrota del Allia. Se apacigua a los espíritus del bosque." },
    "6-20": { status: 'C', statusFull: "Dies Comitialis", deity: "Luna", deityDesc: "La Luna.", festivalName: null, festivalDesc: null },
    "6-21": { status: 'NP', statusFull: "Lucaria II", deity: "Silvanus", deityDesc: "Bosques.", festivalName: "Lucaria", festivalDesc: "Segundo día de ritos en el bosque sagrado, asegurando la protección de la naturaleza salvaje sobre la ciudad." },
    "6-22": { status: 'C', statusFull: "Dies Comitialis", deity: "Concordia", deityDesc: "Dedicación Templo Concordia.", festivalName: null, festivalDesc: null },
    "6-23": { status: 'NP', statusFull: "Neptunalia", deity: "Neptuno", deityDesc: "Dios del agua dulce y el mar.", festivalName: "Neptunalia", festivalDesc: "En el calor del verano, se invoca a Neptuno (dios de las aguas dulces antes que del mar). La gente construye cabañas (umbrae) con ramas de árboles y celebra picnics a la sombra, bebiendo vino mezclado con agua de manantial para evitar la sequía." },
    "6-24": { status: 'N', statusFull: "Dies Nefastus", deity: "Furrina", deityDesc: "Diosa antigua de las aguas subterráneas.", festivalName: "Furronalia", festivalDesc: "Sacrificios a Furrina en su arboleda del Janículo. Diosa arcaica de los manantiales oscuros, cuyo culto era ya oscuro para los romanos tardíos." },
    "6-25": { status: 'C', statusFull: "Dies Comitialis", deity: "Furrina", deityDesc: "Furrina.", festivalName: null, festivalDesc: null },
    "6-26": { status: 'C', statusFull: "Dies Comitialis", deity: "Canis Major", deityDesc: "Salida de Sirio (Canícula).", festivalName: null, festivalDesc: null },
