    "7-13": { status: 'NP', statusFull: "Idus", deity: "Diana", deityDesc: "Diosa de los esclavos y la naturaleza.", festivalName: "Nemoralia", festivalDesc: "Festival de las Antorchas de Diana en el lago Nemi. Los esclavos tienen día libre y las mujeres lavan su cabello. Se agradece a la diosa de la caza y la luna llena." },
    "7-14": { status: 'F', statusFull: "Dies Fastus", deity: "Vertumnus", deityDesc: "Dios del cambio de estaciones.", festivalName: null, festivalDesc: null },
    "7-15": { status: 'C', statusFull: "Dies Comitialis", deity: "Júpiter", deityDesc: "Júpiter.", festivalName: null, festivalDesc: null },
    "7-16": { status: 'C', statusFull: "Dies Comitialis", deity: "Janus", deityDesc: "Jano.", festivalName: null, festivalDesc: null },
    "7-17": { status: 'NP', statusFull: "Portunalia", deity: "Portunus", deityDesc: "Dios de los puertos y llaves.", festivalName: "Portunalia", festivalDesc: "El dios de las puertas y los puertos. Se llevan llaves al Foro para ser bendecidas en una caldera sagrada. Protege los almacenes de grano del Tíber." },
    "7-18": { status: 'C', statusFull: "Dies Comitialis", deity: "Consus", deityDesc: "Dios del almacenamiento de grano.", festivalName: null, festivalDesc: null },
    "7-19": { status: 'NP', statusFull: "Vinalia Rustica", deity: "Júpiter/Venus", deityDesc: "Vino y huertos.", festivalName: "Vinalia Rustica", festivalDesc: "Fiesta de la cosecha de la uva. El Flamen Dialis sacrifica un cordero a Júpiter y corta el primer racimo de uvas, inaugurando la vendimia. Venus protege los jardines y huertos." },
    "7-20": { status: 'C', statusFull: "Dies Comitialis", deity: "Consus", deityDesc: "Consus.", festivalName: "Consualia", festivalDesc: "Preparación Consualia." },
    "7-21": { status: 'F', statusFull: "Dies Fastus", deity: "Consus", deityDesc: "Consus.", festivalName: "Consualia", festivalDesc: "Carreras de mulas y apertura del altar subterráneo de Consus en el Circo Máximo. Se celebra el fin de la cosecha y el almacenamiento seguro del grano." },
    "7-22": { status: 'EN', statusFull: "Vulcanalia", deity: "Vulcano", deityDesc: "Dios del fuego destructivo.", festivalName: "Vulcanalia", festivalDesc: "Para apaciguar el fuego destructivo del verano. La gente arroja peces vivos y animales pequeños a hogueras como sustitutos de sus propias vidas. 'Por nuestras almas, estos peces', dicen." },
    "7-23": { status: 'C', statusFull: "Dies Comitialis", deity: "Volturnus", deityDesc: "Dios del río Volturno.", festivalName: "Volturnalia", festivalDesc: "Sacrificio al dios del río (o viento) Volturnus." },
    "7-24": { status: 'NP', statusFull: "Mundus Patet", deity: "Manes", deityDesc: "Mundo de los muertos abierto.", festivalName: "Mundus Patet", festivalDesc: "Se abre la fosa ritual que conecta con el inframundo (Mundus). Los espíritus de los muertos pueden vagar libremente. No se libra batalla ni se reclutan tropas." },
    "7-25": { status: 'NP', statusFull: "Opiconsivia", deity: "Ops", deityDesc: "Diosa de la riqueza.", festivalName: "Opiconsivia", festivalDesc: "Fiesta de Ops Consiva (la que siembra/almacena) en la Regia. Solo las Vestales y el Pontifex Maximus pueden entrar. Se agradece la riqueza agrícola asegurada." },
    "7-26": { status: 'C', statusFull: "Dies Comitialis", deity: "Piscis Austrinas", deityDesc: "Pez Austral.", festivalName: null, festivalDesc: null },
    "7-27": { status: 'F', statusFull: "Volturnalia", deity: "Volturnus", deityDesc: "Viento del Este / Río.", festivalName: "Volturnalia", festivalDesc: "Fiesta del dios río." },
