    "8-4": { status: 'C', statusFull: "Ludi Romani", deity: "Júpiter", deityDesc: "Júpiter Óptimo Máximo.", festivalName: "Ludi Romani", festivalDesc: "Inicio de los Juegos Romanos, los más antiguos y grandiosos. Dedicados a Júpiter Óptimo Máximo tras victorias militares. Comienzan con una procesión solemne desde el Capitolio hasta el Circo Máximo, llevando las estatuas de los dioses en carros (tensae)." },
    "8-5": { status: 'F', statusFull: "Nonae / Ludi", deity: "Júpiter", deityDesc: "Júpiter.", festivalName: "Ludi Romani", festivalDesc: "Continúan los Juegos Romanos con representaciones teatrales y competiciones atléticas." },
