    "8-13": { status: 'NP', statusFull: "Epulum Jovis", deity: "Júpiter", deityDesc: "Banquete de Júpiter.", festivalName: "Epulum Jovis", festivalDesc: "Banquetes sagrados para la Tríada Capitolina (Júpiter, Juno, Minerva). Los senadores cenan públicamente en el Capitolio imitando a los dioses. Se celebra en el aniversario de la dedicación del Templo de Júpiter." },
    "8-14": { status: 'F', statusFull: "Ludi Romani", deity: "Equorum Probatio", deityDesc: "Prueba de caballos.", festivalName: "Ludi Romani", festivalDesc: "Revisión de la caballería (Equorum Probatio) durante los juegos." },
