    // --- OCTOBER (OCTOBER) ---
    "9-1": { status: 'NP', statusFull: "Kalendae", deity: "Fides", deityDesc: "Fe y Lealtad.", festivalName: "Tigillum Sororium", festivalDesc: "Rito del 'Haz de la Hermana' en honor a Horacio. Celebración de Fides Publica (la Fe Pública), diosa de la palabra dada y los tratados. Los sacerdotes utilizan hilos de lana en sus rituales para simbolizar la unión y el compromiso." },
    "9-2": { status: 'F', statusFull: "Dies Fastus", deity: "Bootes", deityDesc: "Boyero (ocaso).", festivalName: null, festivalDesc: null },
    "9-3": { status: 'C', statusFull: "Dies Comitialis", deity: "Bacchus", deityDesc: "Baco.", festivalName: null, festivalDesc: null },
    "9-4": { status: 'C', statusFull: "Dies Comitialis", deity: "Júpiter / Ceres", deityDesc: "Jeyuno (ayuno) de Ceres.", festivalName: "Ieiunium Cereris", festivalDesc: "Día de ayuno en honor a Ceres, instituido para apaciguar prodigios. Se abstienen de pan y vino hasta la tarde. Es una fiesta de expiación para asegurar que la diosa del grano no abandone a Roma." },
    "9-5": { status: 'C', statusFull: "Ludi Augustales", deity: "Augustus", deityDesc: "Divino Augusto.", festivalName: "Ludi Augustales", festivalDesc: "Juegos en honor al Augusto divinizado." },
    "9-6": { status: 'C', statusFull: "Dies Comitialis", deity: "Manes", deityDesc: "Espíritus.", festivalName: "Mundus Patet", festivalDesc: "La fosa al inframundo se abre por segunda vez en el año." },
    "9-7": { status: 'F', statusFull: "Nonae", deity: "Júpiter Fulgur", deityDesc: "Júpiter del Rayo Diurno.", festivalName: "Ritos de Júpiter", festivalDesc: "Sacrificio a Júpiter Fulgur y Juno Curitis en el Campo de Marte. Se busca protección contra las tormentas de otoño." },
    "9-8": { status: 'F', statusFull: "Dies Fastus", deity: "Corona", deityDesc: "Corona (constelación).", festivalName: null, festivalDesc: null },
    "9-9": { status: 'C', statusFull: "Dies Comitialis", deity: "Genius Publicus", deityDesc: "Genio del Pueblo Romano.", festivalName: "Fausta Felicitas", festivalDesc: "Sacrificios al Genio Público en el Capitolio." },
    "9-10": { status: 'C', statusFull: "Dies Comitialis", deity: "Juno Moneta", deityDesc: "Juno la Avisadora.", festivalName: null, festivalDesc: null },
    "9-11": { status: 'NP', statusFull: "Meditrinalia", deity: "Meditrina", deityDesc: "Diosa de la curación/vino.", festivalName: "Meditrinalia", festivalDesc: "Fiesta de la degustación del vino nuevo mezclado con el viejo para la salud. La gente bebe diciendo: 'Bebo lo nuevo y lo viejo, curo la enfermedad nueva y la vieja'. Se honra la propiedad medicinal del vino." },
    "9-12": { status: 'C', statusFull: "Dies Comitialis", deity: "Augustalis", deityDesc: "Fortuna Redux.", festivalName: "Augustalia", festivalDesc: "Celebración del retorno seguro de Augusto a Roma (Ara Fortunae Reducis) tras sus campañas en Oriente. Se realizan sacrificios para agradecer la paz y estabilidad que trae el emperador." },
    "9-13": { status: 'NP', statusFull: "Fontinalia", deity: "Fons", deityDesc: "Dios de las fuentes.", festivalName: "Fontinalia", festivalDesc: "Se arrojan guirnaldas de flores a las fuentes y se coronan los pozos en honor a Fons. Se celebra la pureza del agua potable." },
    "9-14": { status: 'EN', statusFull: "Dies Endotercissus", deity: "Penates", deityDesc: "Dioses del hogar.", festivalName: null, festivalDesc: null },
    "9-15": { status: 'NP', statusFull: "Idus / Equus October", deity: "Mars", deityDesc: "Marte.", festivalName: "Caballo de Octubre", festivalDesc: "Arcaico sacrificio del Caballo de Octubre a Marte en el Campo de Marte. El caballo derecho de la biga ganadora es sacrificado con una lanza. Su cola se lleva corriendo a la Regia para gotear sangre en el hogar. La cabeza es disputada entre los vecinos de Subura y Sacra Via." },
    "9-16": { status: 'F', statusFull: "Dies Fastus", deity: "Arcturus", deityDesc: "Arturo (estrella).", festivalName: null, festivalDesc: null },
    "9-17": { status: 'C', statusFull: "Dies Comitialis", deity: "Júpiter", deityDesc: "Júpiter.", festivalName: null, festivalDesc: null },
    "9-18": { status: 'C', statusFull: "Dies Comitialis", deity: "Júpiter", deityDesc: "Júpiter.", festivalName: null, festivalDesc: null },
    "9-19": { status: 'NP', statusFull: "Armilustrium", deity: "Mars", deityDesc: "Marte.", festivalName: "Armilustrium", festivalDesc: "Purificación de las armas del ejército antes de guardarlas para el invierno. Los sacerdotes Salii danzan con sus escudos sagrados (ancilia) en el Aventino. Marca el fin oficial de la temporada de campañas militares." },
