    "10-1": { status: 'NP', statusFull: "Kalendae", deity: "Epona", deityDesc: "Diosa de los caballos.", festivalName: "Ludi Circenses", festivalDesc: "Final de los juegos de Sila. Se honra también a Epona, diosa celta de los caballos adoptada por la caballería romana." },
    "10-2": { status: 'F', statusFull: "Dies Fastus", deity: "Manes", deityDesc: "Difuntos.", festivalName: null, festivalDesc: null },
    "10-3": { status: 'C', statusFull: "Dies Comitialis", deity: "Hércules", deityDesc: "Hércules.", festivalName: null, festivalDesc: null },
    "10-4": { status: 'C', statusFull: "Ludi Plebeii", deity: "Júpiter", deityDesc: "Júpiter.", festivalName: "Ludi Plebeii", festivalDesc: "Inicio de los Juegos Plebeyos, establecidos para celebrar la libertad política de la plebe tras la expulsión de los reyes o la reconciliación de órdenes. Teatro y banquetes en honor a Júpiter." },
    "10-5": { status: 'F', statusFull: "Nonae / Ludi", deity: "Júpiter", deityDesc: "Júpiter.", festivalName: "Ludi Plebeii", festivalDesc: "Continúan los Juegos Plebeyos." },
    "10-6": { status: 'NP', statusFull: "Ludi Plebeii", deity: "Júpiter", deityDesc: "Júpiter.", festivalName: "Ludi Plebeii", festivalDesc: "Juegos." },
    "10-7": { status: 'NP', statusFull: "Ludi Plebeii", deity: "Júpiter", deityDesc: "Júpiter.", festivalName: "Ludi Plebeii", festivalDesc: "Juegos." },
    "10-8": { status: 'NP', statusFull: "Mundus Patet", deity: "Manes", deityDesc: "Mundo de los muertos abierto.", festivalName: "Mundus Patet", festivalDesc: "Tercera y última apertura de la fosa ritual que conecta con los Manes. Los espíritus caminan entre los vivos por última vez antes del invierno." },
    "10-9": { status: 'NP', statusFull: "Ludi Plebeii", deity: "Júpiter", deityDesc: "Júpiter.", festivalName: "Ludi Plebeii", festivalDesc: "Juegos." },
    "10-10": { status: 'NP', statusFull: "Ludi Plebeii", deity: "Júpiter", deityDesc: "Júpiter.", festivalName: "Ludi Plebeii", festivalDesc: "Juegos." },
    "10-11": { status: 'NP', statusFull: "Ludi Plebeii", deity: "Júpiter", deityDesc: "Júpiter.", festivalName: "Ludi Plebeii", festivalDesc: "Juegos." },
    "10-12": { status: 'NP', statusFull: "Ludi Plebeii", deity: "Júpiter", deityDesc: "Júpiter.", festivalName: "Ludi Plebeii", festivalDesc: "Juegos." },
    "10-13": { status: 'NP', statusFull: "Epulum Jovis", deity: "Júpiter", deityDesc: "Banquete de Júpiter.", festivalName: "Epulum Jovis", festivalDesc: "Gran banquete sagrado en honor a Júpiter, similar al de septiembre, pero celebrado por los ediles plebeyos. Senadores y magistrados comparten la mesa con los dioses." },
    "10-14": { status: 'F', statusFull: "Equorum Probatio", deity: "Mars", deityDesc: "Caballos.", festivalName: "Equorum Probatio", festivalDesc: "Revisión de la caballería en el Circo Máximo." },
    "10-15": { status: 'NP', statusFull: "Ludi Plebeii", deity: "Feronia", deityDesc: "Diosa de la vida silvestre y la libertad.", festivalName: "Ludi Plebeii / Feronia", festivalDesc: "Festival de Feronia en el Campo de Marte. Diosa que otorga libertad a los esclavos meritorios y preside sobre la vida silvestre." },
    "10-16": { status: 'C', statusFull: "Dies Comitialis", deity: "Júpiter", deityDesc: "Júpiter.", festivalName: "Ludi Plebeii", festivalDesc: "Juegos." },
    "10-17": { status: 'C', statusFull: "Dies Comitialis", deity: "Júpiter", deityDesc: "Júpiter.", festivalName: "Ludi Plebeii", festivalDesc: "Fin de los Juegos Plebeyos." },
    "10-18": { status: 'C', statusFull: "Dies Comitialis", deity: "Ceres", deityDesc: "Ceres.", festivalName: "Mercatus", festivalDesc: "Días de mercado." },
    "10-19": { status: 'C', statusFull: "Dies Comitialis", deity: "Cibeles", deityDesc: "Magna Mater.", festivalName: "Mercatus", festivalDesc: "Mercado." },
    "10-20": { status: 'C', statusFull: "Dies Comitialis", deity: "Proserpina", deityDesc: "Reina del inframundo.", festivalName: "Mercatus", festivalDesc: "Fin del mercado." },
    "10-21": { status: 'C', statusFull: "Dies Comitialis", deity: "Plutón", deityDesc: "Dios de la riqueza oculta.", festivalName: null, festivalDesc: null },
    "10-22": { status: 'C', statusFull: "Dies Comitialis", deity: "Plutón", deityDesc: "Plutón.", festivalName: null, festivalDesc: null },
    "10-23": { status: 'C', statusFull: "Dies Comitialis", deity: "Apollo", deityDesc: "Apolo.", festivalName: null, festivalDesc: null },
    "10-24": { status: 'C', statusFull: "Dies Comitialis", deity: "Brumalia", deityDesc: "Baco.", festivalName: "Brumalia", festivalDesc: "Comienzo de las fiestas de invierno 'Brumalia' (de Bruma, el día más corto). Se celebra con vino y profecías durante todo un mes hasta el solsticio. Baco y Saturno presiden este tiempo de oscuridad creciente." },
    "10-25": { status: 'C', statusFull: "Dies Comitialis", deity: "Hercules", deityDesc: "Hércules.", festivalName: null, festivalDesc: null },
    "10-26": { status: 'C', statusFull: "Dies Comitialis", deity: "Diana", deityDesc: "Diana.", festivalName: null, festivalDesc: null },
    "10-27": { status: 'C', statusFull: "Dies Comitialis", deity: "Luna", deityDesc: "Luna.", festivalName: null, festivalDesc: null },
