    "11-30": { status: 'F', statusFull: "Dies Fastus", deity: "Iuppiter", deityDesc: "Júpiter.", festivalName: null, festivalDesc: null },
    "11-31": { status: 'C', statusFull: "Dies Comitialis", deity: "Janus", deityDesc: "Jano.", festivalName: null, festivalDesc: null },
